import React from 'react';
import "./App.css";

/*
function Phoneme({symbol, easyType, type, notes}){
    return(
        <div className="phoneme">
            <h3>{symbol}</h3>
            <h3>{easyType}</h3>
            <h3>{type}</h3>
            <p>{notes}</p>
        </div>
    );
}
*/

class Phoneme extends React.Component {
    constructor(props){
        super();
        this.symbol = props.symbol; //phonetic symbol
        this.easyType = props.easyType; //Keyboard typeable alternative
        this.type = props.type; //Consonant or Vowel
        this.notes = props.notes;
    }
    
    render() {
        /*
        return(
            <div className="phoneme">
                <h3>{this.symbol}</h3>
                <h3>{this.easyType}</h3>
                <h3>{this.type}</h3>
                <p>{this.notes}</p>
            </div>
        );
        */
        if(this.type == "C"){
            return(
                <Consonant />
            );
        }
        else if(this.type == "V"){
            return(
                <Vowel />
            );
        }
        else{
            return(
                <div className="phoneme">
                    <h3>{this.symbol}</h3>
                    <h3>{this.easyType}</h3>
                    <h3>{this.type}</h3>
                    <p>{this.notes}</p>
                </div>
            );
        }
    }
}
class Consonant extends Phoneme {
    constructor(props){
        super(props);
		this.sol = props.sol; //state of the larynx
		this.poa = props.poa; //place of articulation
		this.moa = props.moa; //manner of articulation
    }
    render(){
        return(
            <div className="phoneme">
                <h3>{this.symbol}</h3>
                <h3>{this.easyType}</h3>
                <h3>{this.type}</h3>
                <p>{this.notes}</p>
            </div>
        );
    }
}
class Vowel extends Phoneme {
    constructor(props){
        super(props);
        this.height = props.height;
        this.backness = props.backness;
        this.rounding = props.rounding;
        this.tenseness = props.tenseness;
    }
    render(){
        return(
            <div className="phoneme">
                <h3>{symbol}</h3>
                <h3>{this.easyType}</h3>
                <h3>{this.type}</h3>
                <p>{this.notes}</p>
            </div>
        );
    }
}
export default Phoneme;
