import React from 'react';
import "./App.css";

class Phoneme extends React.Component {
    constructor(props){
        super();
        this.symbol = props.symbol; //phonetic symbol
        this.easyType = props.easyType; //Keyboard typeable alternative
        this.type = props.type; //Consonant or Vowel
        this.notes = props.notes;
    }
    
    render() {
        /*
        if(this.type == "C"){
            return
        }
        return(
            <div className="phoneme">
                <h3>{this.symbol}</h3>
                <h3>{this.easyType}</h3>
                <h3>{this.type}</h3>
                <p>{this.notes}</p>
            </div>
        );
        */
       
        if(this.type == "C"){
            return(
                <Consonant symbol={this.symbol} easyType={this.easyType} type={this.type} notes={this.notes} sol={this.sol} poa={this.poa} moa={this.moa}/>
            );
        }
        else if(this.type == "V"){
            return(
                <Vowel />
            );
        }
        else{
            return(
                <div className="phoneme">
                    <h3>{this.symbol}</h3>
                    <h3>{this.easyType}</h3>
                    <h3>{this.type}</h3>
                    <p>{this.notes}</p>
                </div>
            );
        }
        
    }
}
class Consonant extends Phoneme {
    constructor(props){
        super(props);
		this.sol = props.sol; //state of the larynx
		this.poa = props.poa; //place of articulation
		this.moa = props.moa; //manner of articulation
    }
    render(){
        return(
            <div className="phoneme">
                <h3>{this.symbol}</h3>
                <h3>{this.easyType}</h3>
                <h3>{this.type}</h3>
                <h3>{this.sol}</h3>
                <h3>{this.poa}</h3>
                <h3>{this.moa}</h3>
                <p>{this.notes}</p>
            </div>
        );
    }
}
class Vowel extends Phoneme {
    constructor(props){
        super(props);
        this.height = props.height;
        this.backness = props.backness;
        this.rounding = props.rounding;
        this.tenseness = props.tenseness;
    }
    render(){
        return(
            <div className="phoneme">
                <h3>{this.symbol}</h3>
                <h3>{this.easyType}</h3>
                <h3>{this.type}</h3>
                <p>{this.notes}</p>
            </div>
        );
    }
}
export default Phoneme;
