import React from 'react';
import "./App.css";

const [phonemes, setPhonemes] = useState([
    {symbol: 'A', easyType: 'a', type: 'V', notes: 'It is a'},
    {symbol: 'B', easyType: 'b', type: 'C', notes: 'b letter'},
    {symbol: 'C', easyType: 'c', type: 'C', notes: 'a c'},
    {symbol: 'I', easyType: 'i', type: 'V', notes: 'an I'}
])

/*
function Phoneme({symbol, easyType, type, notes}){
    return(
        <div className="phoneme">
            <h3>{symbol}</h3>
            <h3>{easyType}</h3>
            <h3>{type}</h3>
            <p>{notes}</p>
        </div>
    );
}
*/

class Phoneme extends React.Component {
    constructor({symbol, easyType, type, notes}){
        super();
        this.symbol = symbol;
        this.easyType = easyType;
        this.type = type;
        this.notes = notes;
    }
    
    render() {
        return(
            <div className="phoneme">
                <h3>{this.symbol}</h3>
                <h3>{this.easyType}</h3>
                <h3>{this.type}</h3>
                <p>{this.notes}</p>
            </div>
        );
    }
  }
  

export default Phoneme;
