import React, {useState, useEffect} from 'react';
import "./App.scss";
import Axios from 'axios';
import Phoneme from './Phoneme';

function MorphemeManager() { 
    const initialData = [
        
    ]

    const [morphemes, setMorphemes] = useState(initialData)

    const [phonemes, setPhonemes] = useState([])

    const getPhonemes = () => {
        new Promise((resolve, reject) => {
            Axios.get("http://localhost:3001/api/get").then((response) => {
                setPhonemes(response.data);
            });
            resolve("updated phonemes from database")
        })
    }

    useEffect(() => {
        getPhonemes();
    }, []);

    return(
    <div class="flexbox-container">
        <h1 align="center">Morphemes</h1>
        {Object.values(phonemes).map(phoneme =>(
            <div>
                <h1>{phoneme.symbol}</h1>
                <h2>Easy Type:{phoneme.easyType}</h2>
                {phoneme.type} === "C" ? <div>
                <h2>{phoneme.sol} {phoneme.poa} {phoneme.moa} Consonant</h2>
                </div>
                : {phoneme.type} === "V" ? <div>
                <h2>{phoneme.height} {phoneme.backness} {phoneme.rounding} Vowel</h2>
                </div>
                : <div>
                <h2>{phoneme.sol} {phoneme.poa} {phoneme.moa} {phoneme.height} {phoneme.backness} {phoneme.rounding}</h2>
                </div>
            </div>
        ))}
    </div>
    )
}
export default MorphemeManager
