import React, {useState} from 'react'

function ConsonantForm(props) {
    const [symbol, setSymbol] = useState("")
    const [easyType, setEasyType] = useState("")
    
    const handleSymbolChange = (event) => {
        setSymbol(event.target.value)
    }

    const handleEasyTypeChange = (event) => {
        setEasyType(event.target.value)
    }

    return (
        <form>
            <div>
                <label>Phonetic Symbol</label>
                <input
                    type="text"
                    value={symbol}
                    onChange={handleSymbolChange}
                />
            </div>
            <div>
                <label>Easy Type</label>
                <input
                    type="textbox"
                    value={easyType}
                    onChange={handleEasyTypeChange}
                />
            </div>
        </form>
    );
}


export default ConsonantForm;
